    SELECT 

	FROM user_zdyfz a


		a.del_flag = #{DEL_FLAG_NORMAL}
		and a.create_by=#{currentUser.id}

SELECT
,u.name AS "linkUser.name", o.name AS "linkUser.officeName" FROM user_zdyfz a LEFT JOIN sys_user u on u.id=a.link_user LEFT JOIN sys_office o on o.id=u.office_id
a.del_flag = #{DEL_FLAG_NORMAL} and a.create_by=#{currentUser.id}

mapper.update:
